import { GET_POSTS, SORT_POST, VOTE_POST, DELETE_POST, CREATE_POST, UPDATE_POST } from '../actions/posts'

const sort = (sortBy = 'timestamp', arr = []) => ( arr.sort((p, b) => p[sortBy] - b[sortBy]));
const clone = obj => JSON.parse(JSON.stringify(obj));

export default function posts (state = {}, action) {
	switch(action.type) {

		case GET_POSTS :
			const get_posts = sort(action.sortBy, action.posts)
			return { ...get_posts }

		case DELETE_POST :
			return { ...Object.values(state).flat().filter(s => s.id !== action.id) }

		case CREATE_POST :
			const create_posts = sort(action.sortBy, Object.values(state).concat(action.post))
			return { ...create_posts };

		case UPDATE_POST :
			const updated_posts = sort(action.sortBy, Object.values(state).filter(p => p.id !== action.post.id).concat(action.post))
			return { ...updated_posts };

		case VOTE_POST :
			let arrState = sort('timestamp', Object.values(clone(state)) );
			arrState.forEach(p => {
				if (p.id === action.id) {
					p.voteScore += action.vote === 'upVote' ? 1 : -1;
				}
			});

			return { ...arrState };

		case SORT_POST :
			const sort_posts = sort(action.sortBy, Object.values(clone(state)) )
			return { ...sort_posts };

		default :
			return state
	}
}
