import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { handleDeletePost } from '../actions/posts';

class PostDetail extends Component {

	componentDidMount() {}

	render() {
		let { post } = this.props;
		post = post ? post : {};

		return (
			<li className='post'>
				<div className="post-header">
					<h4>{post.title}</h4>
					<small>by @{post.author}</small>
					<small>{post.category}</small>
				</div>

				<div className="post-body">
					{post.body}
				</div>

				<div className="post-actions">
					<Link to={`/${post.category}/${post.id}/edit`}>
						<button className='btn-edit'><MdEdit /></button>
					</Link>
					<button className='btn-delete' onClick={() => this.deletePost(post.id)}>
						<MdDeleteForever />
					</button>
				</div>

				<div className="post-footer">
					<small>votes: {post.voteScore}</small>
					<small>comments: {post.commentCount}</small>
				</div>
			</li>
		);
	}

	deletePost(id) {
		if (window.confirm("Are you sure?")) {
			this.props.dispatch( handleDeletePost(id) );
			this.props.history.push('/');
		}
	}
}

export default connect()(PostDetail);
