import React, { Component } from "react";
import { Redirect } from 'react-router'
import { connect } from 'react-redux'

import { handleGetCommentsByPostId, handleCreateComment } from '../actions/comments';

import Comment from './Comment'

class Comments extends Component {

	state = { text: '' }

	constructor(props) {
		super(props)
		const { post, dispatch } = props

		if (Object.keys(post).length > 0) {
			dispatch( handleGetCommentsByPostId(post.id) )
		}
	}

	render() {
		const { post, comments } = this.props;

		if (Object.keys(post).length <= 0) {
			return <Redirect to='/' />
		}

		return (
			<div className='comments'>
				<h4>Comments</h4>
				<hr />

				<div>
					<form className='new-post' onSubmit={this.handleSubmit}>
						<textarea className='textarea' value={this.state.text}
							onChange={ (e) => this.setState({ text: e.target.value }) }
							placeholder="Tell us more about this..." />

						<button className='btn' disabled={this.state.text === ''}
								onClick={this.onCreate.bind(this)}>Submit</button>
					</form>

					<ul>
						{ comments.map(c => <Comment key={c.id} id={c.id} /> ) }
					</ul>
				</div>
			</div>
		);
	}

	onCreate(e) {
		e.preventDefault();

		const { post_id, dispatch } = this.props
		dispatch( handleCreateComment(post_id, this.state.text) );

		this.setState({ text: "" })
	}
}

const mapStateToProps = ({ post, comments }) => {
	comments = post ? comments.filter(c => c.parentId === post.id) : comments;
	return { post, comments };
};

export default connect(mapStateToProps)(Comments);
