import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { MdEdit, MdDeleteForever, MdModeComment, MdThumbDown, MdThumbUp } from "react-icons/md";

import { handleDeletePost } from '../actions/posts';
import { handleGetCommentsByPostId } from '../actions/comments';

import PostFooter from './PostFooter'
import Comments from './Comments'

class PostDetail extends Component {

	constructor(props) {
		super(props)
		const { post, dispatch } = props

		dispatch( handleGetCommentsByPostId(post.id) )
	}

	render() {
		const { post } = this.props;

		return (
			<div style={{ width: '100%' }}>
				<div className='post'>
					<div className="post-header">
						<h4>{post.title}</h4>
						<small>by @{post.author}</small>
						<Link to={`/${post.category}`}>
							<small>{post.category}</small>
						</Link>
					</div>

					<div className="post-body">
						{post.body}
					</div>

					<PostFooter id={post.id} />
				</div>

				<Comments post_id={post.id} />
			</div>
		);
	}

}

export default connect()(PostDetail);
