import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { handleGetCommentsByPostId } from '../actions/comments';

import Comment from './Comment'

class Comments extends Component {

	state = { body: '' }

	constructor(props) {
		super(props)
		const { post_id, dispatch } = props

		dispatch( handleGetCommentsByPostId(post_id) )
	}

	render() {
		const comments = [];

		return (
			<div className='comments'>
				<h4>Comments</h4>
				<hr />

				<div>
					<form className='new-post' onSubmit={this.handleSubmit}>
						<textarea className='textarea'
							onChange={ (e) => this.setState({ body: e.target.value }) }
							placeholder="Tell us more about this..." />

						<button className='btn' type='submit' onClick={this.onCreate}>Submit</button>
					</form>

					<ul>
						{ comments.map(c => <Comment id={c.id} /> ) }
					</ul>
				</div>
			</div>
		);
	}

	onCreate(e) {

		debugger
		var a = this.props;

	}
}

const mapStateToProps = ({ comments }, { post_id }) => {
	comments = Object.values(comments).filter(c => c.parent === post_id);
	return { post_id, comments };
};

export default connect(mapStateToProps)(Comments);
