import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

class Comments extends Component {

	render() {
		const { comments } = this.props;

		return (
			<div className='comments'>
				<h4>Comments</h4>
				<hr />

				<div>
					<form className='new-post' onSubmit={this.handleSubmit}>
						<input type="text" value={title}
							placeholder="What's happening?"
							onChange={ (e) => this.setState({ title: e.target.value }) }
							className='textfield'
							maxLength={280} />

						<textarea
							placeholder="Tell us more about this..."
							value={body}
							onChange={ (e) => this.setState({ body: e.target.value }) }
							className='textarea' />

						<button className='btn' type='submit' disabled={title === '' || body === ''}>
							Submit
						</button>
					</form>
				</div>
			</div>
		);
	}
}

const mapStateToProps = ({ comments }, { post_id }) => {
//	comments = Object.values(comments).filter(c => c.parent === post_id);
	return { post_id, comments };
};

export default connect(mapStateToProps)(Comments);
