import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { MdAddCircleOutline } from "react-icons/md";

import { getPosts } from '../actions/posts'
import Post from './Post';

class Posts extends Component {

	componentDidMount() {}

	render() {
		const posts = this.props.posts.map(post => <Post key={post.id} id={post.id} />);

		return (
			<div className='posts'>

				<div className="sort-posts">
					<strong>Sort by:</strong>
					<span>
						<button onClick={() => this.sortBy('timestamp')}>Date</button>
						<button onClick={() => this.sortBy('voteScore')}>Votes</button>
						<button onClick={() => this.sortBy('commentCount')}>Comments</button>
					</span>
					<hr />
				</div>

				{ this.props.title ?
					<div>
						<h3>{this.props.title}</h3>
						<Link to={`/${this.props.title}/post/new`}>
							<button className='add'>
								<MdAddCircleOutline />
								<small>new post</small>
							</button>
						</Link>
					</div>
					: 
					<h3>All Posts</h3>
				}

				<ul>
					{ posts.length > 0 ? posts : 
						<div>
							<hr />
							There is no posts
							<hr />
						</div>
					}
				</ul>
			</div>
		);
	}

	sortBy(prop) {
		const posts = this.props.posts.sort((p, b) => p[prop] - b[prop])
		this.props.dispatch( getPosts(posts, prop) );
	}
}

export default connect(({ postsÂ }) => ({
	posts: Object.values(posts).flat()
}))(Posts);
