import { GET_POSTS, DELETE_POST, CREATE_POST, UPDATE_POST } from '../actions/posts'

export default function posts (state = {}, action) {
  switch(action.type) {
    case GET_POSTS :
      return {
        ...action.posts.sort((p, b) => p[action.sortBy] - b[action.sortBy])
      }

    case DELETE_POST :

      return { ...Object.values(state).flat().filter(s => s.id !== action.id) }

    case CREATE_POST :

      return Object.values(state).concat(action.post)

    case UPDATE_POST :

      return Object.values(state).filter(p => p.id !== action.post.id).concat(action.post);

    default :
      return state
  }
}
