import React, { Component } from 'react'
import { connect } from 'react-redux'
import PageNotFound from './PageNotFound'

import Posts from '../components/Posts'
import Categories from '../components/Categories'

class CategoryPage extends Component {
	render() {
		const { category, categories } = this.props

		if (!category) {
			return <PageNotFound />
		}

		return (
			<div className="grid-container">
				<Categories categories={categories} />
				<Posts category={category.name} />
			</div>
		)
	}
}

function mapStateToProps ({ categories, posts }, props) {
	let { category } = props.match.params

	categories = Object.values(categories).flat();
	category = categories.find(c => c.name === category)

	if (category)
		posts = Object.values(posts).flat().filter(p => p.category === category.name);

	return {
		category,
		categories,
		posts
	}
}

export default connect(mapStateToProps)(CategoryPage);
