import * as api from '../utils/api'

export const GET_POST = 'GET_POST'

const getPost = (post) => ({ type: GET_POST, post });

export function handleGetPost (id) {
	return (dispatch) => {
		return api.getPost(id).then(post => dispatch( getPost(post) ));
	}
}
