import { GET_COMMENTS, DELETE_COMMENT, CREATE_COMMENT } from '../actions/comments'

export default function comments (state = [], action) {
	switch(action.type) {
		case GET_COMMENTS :
			debugger
			return [ ...action.comments ]

		case DELETE_COMMENT :
			return [ ...state.flat().filter(s => s.id !== action.id) ]

		case CREATE_COMMENT :
			return [ 
				...state, 
				action.comment
			];

		default :
			return state
	}
}
