import React, { Component } from "react";
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { MdAddCircleOutline } from "react-icons/md";

import { getPosts } from '../actions/posts'
import Post from './Post';

class Posts extends Component {

	componentDidMount() {}

	render() {
		return (
			<div className='posts'>

				<div className="sort-posts">
					<strong>Sort by:</strong>
					<span>
						<button onClick={() => this.sortBy('timestamp')}>Date</button>
						<button onClick={() => this.sortBy('voteScore')}>Votes</button>
						<button onClick={() => this.sortBy('commentCount')}>Comments</button>
					</span>
					<hr />
				</div>

				{ this.props.category ?
					<div>
						<h3>{this.props.category}</h3>
						<Link to={`/${this.props.category}/post/new`}>
							<button className='add'>
								<MdAddCircleOutline />
								<small>new post</small>
							</button>
						</Link>
					</div>
					: 
					<h3>All Posts</h3>
				}

				<ul>
					{ 
						this.props.posts.map(post => (
							<Post key={post.id} id={post.id} />
						))
					}
				</ul>
			</div>
		);
	}

	sortBy(prop) {
		const posts = this.props.posts
		this.props.dispatch( sortPosts(posts, prop) );
	}
}

export default connect(({ postsÂ }, { category }) => ({
	category,
	posts: category 
			? Object.values(posts).flat().filter(p => p.category === category) 
			: Object.values(posts).flat()
}))(Posts);
