import { GET_COMMENTS, DELETE_COMMENT, CREATE_COMMENT } from '../actions/comments'

const clone = obj => JSON.parse(JSON.stringify(obj));

export default function comments (state = {}, action) {
	switch(action.type) {
		case GET_COMMENTS :
			debugger
			const get_comments = sort(action.sortBy, action.comments)
			return { ...get_comments }

		case DELETE_COMMENT :
			return { ...Object.values(state).flat().filter(s => s.id !== action.id) }

		case CREATE_COMMENT :
			const create_comments = sort(action.sortBy, Object.values(state).concat(action.comment))
			return { ...create_comments };

		default :
			return state
	}
}
