import React, { Component } from "react";
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

import { MdThumbDown, MdThumbUp } from "react-icons/md";

class Comment extends Component {

	render() {
		const { comment } = this.props;

		return (
			<li className="comment">
				<div style={{ display: 'flex', justifyContent: 'space-between' }}>
					<div style={{ padding: '5px 10px 0', fontWeight: 500 }}>
						@{comment.author}
					</div>

					<div style={{ padding: '5px 10px 0' }}>
						<button className='vote'>
							<MdThumbUp />
						</button>
						<small>votes: {comment.voteScore}</small>
						<button className='vote'>
							<MdThumbDown />
						</button>
					</div>
				</div>

				<p> { comment.body } </p>

				<div className="post-actions">
					<Link to={`/${post.category}/${post.id}/edit`}>
						<button className='btn-edit'><MdEdit /></button>
					</Link>
					<button className='btn-delete' onClick={() => this.deletePost(post.id)}>
						<MdDeleteForever />
					</button>
				</div>
			</li>
		);
	}
}

const mapStateToProps = ({ comments }, { id }) => {
	const comment = comments.find(c => c.id === id);
	return { id, comment };
};

export default connect(mapStateToProps)(Comment);
